Get-ADUser -Filter { Enabled -eq $true } -Properties Displayname,Surname,Givenname,Company,EmailAddress,Title,Department,Manager -SearchBase "OU=Employees,OU=Accounts,DC=BGS-LLC,DC=Com" -SearchScope Subtree | select Displayname,Surname,Givenname,Company,EmailAddress,Title,Department,Manager, @{n="ManagerName";e={get-aduser $_.manager | select -ExpandProperty name}}, @{n="ManagerMail";e={get-aduser $_.manager -properties mail | select -ExpandProperty mail}} | Export-Csv c:\temp\AllUserManagers.csv